import React from 'react';
import ReactDOM from 'react-dom';
import Hello from 'C:/Users/DELL/hello-project/src/Component.jsx'


class Welcome extends React.Component {
    constructor(props){
        super(props);
        this.state = {value :''};

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(event){
        this.setState({value: event.target.value});
    }

    handleSubmit(){
        this.Hello.name.setState(this.state.value);
    }

    render (){
        return(
            <form onSubmit ={this.handleSubmit()} >
                <input type = "text" value = {this.setState.value}  name='nom' />
                <input type = "submit" value = "Submit" onClick = {this.handleChange}/>
                <Hello name = {this.state.value}/>
            </form>
        )
    }
}
const element = <Welcome  />

ReactDOM.render(
    element,
    document.getElementById('root')
  );
